import logo from './logo.svg';
import './App.css';

import DeroBridgeApi from './api.js'
import React from 'react'
import ReactDOM from 'react-dom'
import to from 'await-to-js'

const App = () => {

   const [firstMember, setFirstMember] = React.useState("");
   const [secondMember, setSecondMember] = React.useState("");
   const [thirdMember, setThirdMember] = React.useState("");
   const [fourthMember, setFourthMember] = React.useState("");
   const [fifthMember, setFifthMember] = React.useState("");

  const deroBridgeApiRef = React.useRef()
  const [bridgeInitText, setBridgeInitText] = React.useState('')

  React.useEffect(() => {
    const load = async () => {
      deroBridgeApiRef.current = new DeroBridgeApi()
      const deroBridgeApi = deroBridgeApiRef.current
      const [err] = await to(deroBridgeApi.init())
      if (err) {
        setBridgeInitText('failed to connect to extension')
      } else {
        setBridgeInitText('connected to extension')
      }
    }

    window.addEventListener('load', load)
    return () => window.removeEventListener('load', load)
  }, [])

  const transfer = React.useCallback(async () => {
    const deroBridgeApi = deroBridgeApiRef.current
    const [err, res] = await to(deroBridgeApi.wallet('start-transfer', {
    	"scid": "8076c7d1fb85a5ab0f022a0ce30de389665ef5c199c2c1b3f23b22b50b1036fa",
    	"ringsize": 2,
    	"sc_rpc": [{
    		"name": "entrypoint",
    		"datatype": "S",
    		"value": "AppointFiveMembers"
    	},
        {
            "name": "address0",
            "datatype": "S",
            "value": firstMember
        },
        {
            "name": "address1",
            "datatype": "S",
            "value": "deto1qyx7qyvrtrhvaszeej487k2g689fav7h38ay37fja9qf40ycgl0m2qg8ap2y9"
        },
        {
            "name": "address2",
            "datatype": "S",
            "value": "deto1qy2nxgts7wdn28ckc4l2tewphjcppqjfj69ddkxjn0ay8hlsjx73jqgmat5s8"
        },
        {
            "name": "address3",
            "datatype": "S",
            "value": "deto1qyzj2ldw7cjpprhf84ljmxdw54w72jekfkwt0nqsyyzgatttg37d2qgefmama"
        },
        {
            "name": "address4",
            "datatype": "S",
            "value": "deto1qy3kdan2gr7nj275vhlf2kaxhqt5fzjw8phj0f8l7dcms9yqdwzywqg69qdgc"
        }]
    }))

    console.log(err)
    console.log(res)
  }, [])

  const appoint = (event) => {
  	event.preventDefault();
  	setFirstMember(event.target.m1.value);
  	setSecondMember(event.target.m2.value);
  	setThirdMember(event.target.m3.value);
  	setFourthMember(event.target.m4.value);
  	transfer();
  }

  const getBalance = React.useCallback(async () => {
    const deroBridgeApi = deroBridgeApiRef.current
    const [err, res] = await to(deroBridgeApi.wallet('get-balance'))
    if (err) alert(err.message)
    else alert(JSON.stringify(res))
  })

  return <div>
    <div>{bridgeInitText}</div>
    <h3> Appoint Five Board Members </h3>
    <form onSubmit={appoint}>
    <p>Member 1 </p>
    <input id="m1" type="text" />
    <p>Member 2 </p>
    <input id="m2" type="text" />
    <p>Member 3 </p>
    <input id="m3" type="text" />
    <p>Member 4 </p>
    <input id="m4" type="text" />
    <p>Member 5 </p>
    <input id="m5" type="text" />
    <button type={"submit"}>Appoint</button>
    </form>
    <button onClick={getBalance}>Get balance</button>
  </div>
}



export default App;
